                    if (TeleporterFromTo(tele, new List<Rectangle2D>() { Utility.World.SolenCaves }, new List<Rectangle2D>() { Utility.World.SolenCaves }))
                    {
                        tele.Running = false;
                        count++;
                    }
            }

            return count;
        }
