        public static Item FindItem(Type t, Map map, Point3D p)
        {
            Item found = null;

            IPooledEnumerable eable = map.GetItemsInRange(p, 0);
            foreach (Item item in eable)
                if (item.GetType().IsAssignableFrom(t))
                {
                    found = item;
                    break;
                }

            eable.Free();

            return found;
        }
